package week04;

public class Rect {
	
	public static double calcRectArea(double h, double w) {
		return h*w;
	}

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		double area = calcRectArea(12.0, 2.0);
		System.out.println(area);
		System.out.println("Hello wrold");
	}

}
